class WynikBadania extends BaseWynikBadania
{
  protected static $oceny = array(
    'wz'  => 'Wznowa',
    'stg' => 'Stagnacja',
    'pr'  => 'Progresja',
    'bcg' => 'Brak cech guza'
  );

  public function getOcenaStanuGuzaWord()
  {
    return self::$oceny[$this->getOcenaStanuGuza()];
  }

  /**
   * @param string $type
   * @return Widmo
   */
  public function getWidmo($type = self::WZGORZE)
  {
    foreach ($this->getWidma() as $widmo)
    {
      if ($type == $widmo->getLokalizacja())
      {
        return $widmo;
      }
    }
  }
}
